from selene import have, be
import pytest


QUERY_STRING = ""


@pytest.fixture
def open_and_close_browser(browser_config):
	browser = browser_config
	browser.open('/')
	yield browser
	browser.quit()

def test_homework(open_and_close_browser):
	browser = open_and_close_browser
	assert browser.driver.title == "Google"

	browser.element("textarea[name='q']").type(QUERY_STRING)
	browser.all("div").element_by(have.text("Пожаловаться на неприемлемые подсказки")).should(be.hidden)


